                      data_aplicacao: new Date().toISOString().split('T')[0],
                      aplicacao: 'Individual',
                      tipo_habilitacao: 'Psicológica',
                      observacoes: avaliacaoType === 'anonymous' ? 'Avaliação anônima' : ''
